import React, { useEffect, useState } from 'react';

export default function Mobiliario(props) {

    const { mobiliarios, setMobiliarios } = props;
    const [codigo, setCodigo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [editingId, setEditingId] = useState(null);

    const resetMobiliarios = () => {
        setMobiliarios(null)
    }

    useEffect(() => {
        // FunciÃ³n para obtener todos los mobiliarios al cargar el componente
        fetch('/mobiliarios')
            .then(response => response.json())
            .then(data => setMobiliarios(data))
            .catch(error => console.error('Error fetching mobiliarios:', error));
    }, [setMobiliarios]);

    const handleCreate = () => {
        const newMobiliario = { codigo, descripcion };

        fetch('/mobiliarios', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(newMobiliario)
        })
        .then(response => response.json())
        .then(createdMobiliario => {
            setMobiliarios([...mobiliarios, createdMobiliario]);
            setCodigo('');
            setDescripcion('');
        })
        .catch(error => console.error('Error creating mobiliario:', error));
    };

    const handleDelete = (id) => {
        fetch(`/mobiliarios/${id}`, {
            method: 'DELETE'
        })
        .then(() => {
            setMobiliarios(mobiliarios.filter(mobiliario => mobiliario.id !== id));
        })
        .catch(error => console.error('Error deleting mobiliario:', error));
    };

    const handleUpdate = () => {
        const updatedMobiliario = { codigo, descripcion };

        fetch(`/mobiliarios/${editingId}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(updatedMobiliario)
        })
        .then(response => response.json())
        .then(updatedMobiliario => {
            setMobiliarios(mobiliarios.map(mobiliario => (mobiliario.id === editingId ? updatedMobiliario : mobiliario)));
            setCodigo('');
            setDescripcion('');
            setEditingId(null);
        })
        .catch(error => console.error('Error updating mobiliario:', error));
    };

    const startEdit = (mobiliario) => {
        setCodigo(mobiliario.codigo);
        setDescripcion(mobiliario.descripcion);
        setEditingId(mobiliario.id);
    };
    
    return (
    <div>
        <h1>Listado de Mobiliario</h1>
        <br></br>
        <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal" data-bs-whatever="@mdo">Crear Mobiliario</button>
        <br></br>
        <table class="table">
            <thead>
                <tr>
                <th scope="col">#</th>
                <th scope="col">codigo</th>
                <th scope="col">Descripcion</th>
                <th scope="col">Acciones</th>
                </tr>
            </thead>
            <tbody>
                {mobiliarios.map((mobiliario, index) => (
                    <tr>
                        <td>{mobiliario.id}</td>
                        <td>{mobiliario.codigo}</td>
                        <td>{mobiliario.descripcion}</td>
                        <td>
                            <button class="btn btn-secondary" id="btnEditar"   >Editar</button>
                            <button class="btn btn-danger" >Elimnar</button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>

        <span class="btn btn-link" onClick={resetMobiliarios}>Volver a Home</span>

        <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h5 class="modal-title" id="exampleModalLabel">Nuevo Mobiliario</h5>
              <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div class="modal-body">
              <form>
                <div class="mb-3">
                  <label for="recipient-name" class="col-form-label">Codigo:</label>
                  <input type="text" class="form-control" id="recipient-name" name="codigo" />
                </div>
              </form>
              <form>
                <div class="mb-3">
                  <label for="recipient-name" class="col-form-label">Descripcion:</label>
                  <input type="text" class="form-control" id="recipient-name" name="descripcion" />
                </div>
              </form>
            </div>
            <div class="modal-footer">
              <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Cerrar</button>
              <button type="button" class="btn btn-primary">Guardar Mobiliario</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
